import React,{useState,useEffect} from 'react';
import {Modal,Button,Form} from 'react-bootstrap'

function AddAmountModal(props) {

  const [show, setShow] = useState(false);
  const [items, setItems] = useState([]);
  const [amount, setAmount] = useState(5000);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const AmountValue = (e) => setAmount(e.target.value);

  useEffect(()=>{
    const itemsToAdd=[]
    for (var i=props.min;i<=props.max;i=i+props.min){
      itemsToAdd.push(i)
    }
    setItems(itemsToAdd)
  },[props.max,props.min]);

  const handleAdd = (event) => {
    event.preventDefault();
    setShow(false)
    UpdateAmount();
    props.refresh()
  }

  const UpdateAmount = async() => {
    try {
      const data = {"algoname":props.name,"amount":{amount}.amount,"action":"add"}
      const response = await fetch('http://localhost:5000/updateAmount', {
        method: 'POST', // or 'PUT'
        body: JSON.stringify(data), // data can be `string` or {object}!
        mode:'no-cors',
        headers: {
          'Content-Type': 'application/json'
        }
      });
      //const json = await response.json();
      //console.log('Success:', JSON.stringify(json));
    } catch (error) {
      //console.error('Error:', error);
    }
  }


  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Add
      </Button>
      <Form>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{props.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body>

          <Form.Group controlId="exampleForm.ControlSelect1">
            <Form.Label>Amount To Add</Form.Label>
            <Form.Control onChange = {AmountValue} as="select">
              {items.map((item,key)=>(
                <option>{item}</option>
              ))}

            </Form.Control>
          </Form.Group>

        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button type = "submit"variant="primary" onClick={handleAdd}>
            Add Amount
          </Button>
        </Modal.Footer>
      </Modal>
      </Form>
    </>
  );
}


export default AddAmountModal;
