import React from 'react';
import {Table,Col} from 'react-bootstrap';
import {USDFormat} from '../libs/numberFormat'
import {Deposit} from './Deposit'
import {Withdraw} from './Withdraw'
import config from '../config';
import NPVChart from './NPVChart'

class Summary extends React.Component {
  constructor(props){
    super(props);
    this.state={
      pnl:0.0,
      npv:0.0,
      cashDep:0.0,
      amountAlloc:0.0,
      usagePer:"0.0%",
      amountFree:0.0
    }
  }

  componentDidMount() {
    async function FetchSummary(){
      const data = {"userid":this.props.userid}
      await fetch(config.apiGateway.URL+"/getsummary", {
          method: 'POST', // or 'PUT'
          body: JSON.stringify(data), // data can be `string` or {object}!
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
          }
        })
        .then(response=>response.json())
        .then(data=>{
          console.log("Fetching summary API")
          console.log(data)
          this.setState({
            pnl:data.pnl,
            npv:data.npv,
            cashDep:data.accountbalance,
            amountAlloc:data.amountallocated,
            usagePer:data.usagelevel,
            amountFree:data.amountfree
          });

        })
        .catch(error=>console.log(error));
    }
    FetchSummary();
  }


  render (){
    return (
      <div>
          <Table striped bordered hover>
            <tbody>

              <tr>
                <td>Profit/Loss</td>
                <td>{USDFormat(this.state.pnl)}</td>
              </tr>
              <tr>
                <td>NPV</td>
                <td>{USDFormat(this.state.npv)}</td>
              </tr>
              <tr>
                <td>Cash Deposited&nbsp;&nbsp;&nbsp;<Deposit userid={this.props.userid} min={5000} max={50000}/></td>
                <td>{USDFormat(this.state.accountbalance)}</td>
              </tr>

              <tr>
                <td>Amount Allocated</td>
                <td>{USDFormat(this.state.amountAlloc)}</td>
              </tr>
              <tr>
                <td>Usage (%)</td>
                <td>{this.state.usagePer}</td>
              </tr>
              <tr>
                <td>Amount Free&nbsp;&nbsp;&nbsp;<Withdraw userid={this.props.userid} min={0} max={this.state.amountFree}/></td>
                <td>{USDFormat(this.state.amountFree)}</td>
              </tr>

            </tbody>
          </Table>
          <Col><NPVChart npvchart={this.props.npvChart}/></Col>
      </div>
    );
  }
}

export default Summary;
