import React from 'react';
import {Alert} from 'react-bootstrap';
import config from '../config';
import { Auth } from "aws-amplify";
import './Dashboard.css';


class Admin extends React.Component{
  constructor(props){
    super(props);
    this.state={
      isLoaded:false,
      accounttype:sessionStorage.getItem("accounttype"),
      userid:sessionStorage.getItem("userid"),
      adminsummary:{}
    };
    this.refreshAdmin = this.refreshAdmin.bind(this)
  }


  async refreshAdmin(){
    function sleepSeconds(ms) {
      return new Promise(resolve => setTimeout(resolve, ms));
    }
    await sleepSeconds(1000)
    const data = {"userid":this.state.userid,"accounttype":this.state.accounttype}
    await fetch(config.apiGateway.URL+"/getadmin", {
        method: 'POST', // or 'PUT'
        body: JSON.stringify(data), // data can be `string` or {object}!
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      })
      .then(response=>response.json())
      .then(data=>{
        console.log("Fetching admin API")
        console.log(data)
        this.setState({
          adminsummary:data.adminsummary,
        });
        this.setState({isLoaded:true})

      })
      .catch(error=>console.log(error));
  }

  async componentDidMount(){
    this.refreshAdmin()

  }





  render(){

    return (

      <div style={{'background-color': '#000000'}} >
        {this.state.isLoaded
        ?
        <div style={{'background-color': '#000000'}}>
          {this.state.accounttype==="Live"
          ?
          <Alert variant="primary">
            <Alert.Heading>Live Account</Alert.Heading>
          </Alert>
          :
          <Alert variant="danger">
            <Alert.Heading>Paper Account</Alert.Heading>
          </Alert>

          }

        <Card bg="dark">
        <Row>
          <Col md="auto" lg="4" >
          <Summary summary={this.state.summary} refresh={this.refreshDashboard} />
          </Col>
        </Row>
        </Card>
        <br></br>



      </div>
      :
      <div style={{'background-color': '#000000'}}>
        <Container>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <Row>
        <Col></Col>
        <Col md="auto">
          <Spinner animation="border" variant ="info" role="status">
            <span className="sr-only">Loading...</span>
          </Spinner>
        </Col>
        <Col></Col>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>
        <br></br>

        </Row>
        </Container>
      </div>
      }
      </div>
    );
  }
}

export default Admin;
