import React,{useEffect,useState} from 'react';
import ReactApexChart from 'apexcharts'

import Chart from "react-apexcharts";

class BackTestWeekly extends React.Component {
    constructor(props) {
      super(props);

      this.state = {

        series: [{
          name: 'Weekly PnL',
          data: Object.values(props.chart)
        }],
        options: {

          chart: {
            foreColor: '#ffffff',
            type: 'bar',
            height: 350
          },
          plotOptions: {
            bar: {
              horizontal: false,
              columnWidth: '55%',
              endingShape: 'rounded'
            },
          },
          dataLabels: {
            enabled: false
          },
          stroke: {
            show: true,
            width: 2,
            colors: ['transparent']
          },
          xaxis: {
            categories: Object.keys(props.chart),
          },
          yaxis: {
            title: {
              text: '$ (thousands)'
            }
          },
          fill: {
            opacity: 1
          },
          tooltip: {
            theme:"dark",
            y: {
              formatter: function (val) {
                return "$ " + val + " thousands"
              }
            }
          }
        },


      };
    }



    render() {
      return (


            <div id="chart">
                <Chart options={this.state.options} series={this.state.series} type="bar" height={350} />
            </div>


      );
    }
  }

  export default BackTestWeekly;
