import React from 'react';
import {Table} from 'react-bootstrap';
import {USDFormat} from '../libs/numberFormat'
import {Deposit} from './Deposit'
import {Withdraw} from './Withdraw'

function Summary(props) {

  const [pnl,setPnl] = useState(0.0);
  const [npv,setNpv] = useState(0.0);
  const [cashDep,setCashDep] = useState(0.0);
  const [amountAlloc,setAmountAlloc] = useState(0.0);
  const [usagePer,setUsagePer] = useState("0%");
  const [amountFree,setAmountFree] = useState(0.0);

  useEffect(()=>{
    async function FetchSummary(){
      const data = {"userid":props.userid}
      await fetch(config.apiGateway.URL+"/getsummary", {
          method: 'POST', // or 'PUT'
          body: JSON.stringify(data), // data can be `string` or {object}!
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json'
          }
        })
        .then(response=>response.json())
        .then(data=>{
          console.log("Fetching summary API")
          console.log(data)
          setPnl(data.pnl)
          setNpv(data.npv)
          setCashDep(data.accountbalance)
          setAmountAlloc(data.pnl)
          setUsagePer(data.pnl)
          setAmountFree(data.pnl)

        })
        .catch(error=>console.log(error));
    }
    FetchSummary();
  },[props.userid]);

  return (
    <div>
        <Table striped bordered hover>
          <tbody>

            <tr>
              <td>Profit/Loss</td>
              <td>{USDFormat(props.summary.pnl)}</td>
            </tr>
            <tr>
              <td>NPV</td>
              <td>{USDFormat(props.summary.npv)}</td>
            </tr>
            <tr>
              <td>Cash Deposited&nbsp;&nbsp;&nbsp;<Deposit userid={props.userid} min={5000} max={50000}/></td>
              <td>{USDFormat(props.summary.accountbalance)}</td>
            </tr>

            <tr>
              <td>Amount Allocated</td>
              <td>{USDFormat(props.summary.amountallocated)}</td>
            </tr>
            <tr>
              <td>Usage (%)</td>
              <td>{props.summary.usagelevel}</td>
            </tr>
            <tr>
              <td>Amount Free&nbsp;&nbsp;&nbsp;<Withdraw userid={props.userid} min={0} max={props.summary.amountfree}/></td>
              <td>{USDFormat(props.summary.amountfree)}</td>
            </tr>

          </tbody>
        </Table>
    </div>
  );
}

export default Summary;
