import React,{useEffect,useState} from 'react';
//import ApexCharts from 'apexcharts'

import Chart from "react-apexcharts";

function AlgoPNLChart(props){

  const [options,setOptions] = useState({});
  const [series,setSeries] = useState([]);

  useEffect(()=>{
    let op = props.chart.map(e=>{
      let key = e.x
      let value=e.y
      console.log("XXXX")
      console.log(key)
      console.log(value)
      return { x: new Date(key).getTime(), y: value }
    })
    setOptions({
      chart: {
        zoom: {
            enabled: true,
            type: 'x',  
            autoScaleYaxis: false,  
            zoomedArea: {
              fill: {
                color: '#90CAF9',
                opacity: 0.4
              },
              stroke: {
                color: '#0D47A1',
                opacity: 0.4,
                width: 1
              }
            }
        }
      },
      xaxis:{
        type: "datetime",
        labels: {
          formatter: function(value, timestamp) {
            let current_datetime = new Date(timestamp)
            let formatted_date =  current_datetime.getMonth() + "-" + current_datetime.getDate() 
            //let formatted_date = current_datetime.getFullYear() + "-" + (current_datetime.getMonth() + 1) + "-" + current_datetime.getDate() + " " + current_datetime.getHours() + ":" + current_datetime.getMinutes() + ":" + current_datetime.getSeconds() 

            return new Date(formatted_date)
          }
        }
      }

      })
    setSeries([
      {
        name: "PNL",
        data: op
      }])
  },[props.chart]);



    return (
      <div className="app">
        <div >
          <div className="mixed-chart">
            <Chart
              options={options}
              series={series}
              type="area"
              width="100%"
            />
          </div>
        </div>
      </div>
    );
  }


export default AlgoPNLChart;
