import React,{useEffect,useState} from 'react';
import ApexCharts from 'apexcharts'

import Chart from "react-apexcharts";

function PositionChart(props){

  const [options,setOptions] = useState({});
  const [series,setSeries] = useState([]);

  useEffect(()=>{
    setOptions({
        chart: {
          id: "basic-line"
        },
        annotations: {
          xaxis:[{
            points: [{
              x: 20191222,
              y: 52000,
              marker: {
                size: 8,
                fillColor: '#fff',
                strokeColor: 'red',
                radius: 2,
                cssClass: 'apexcharts-custom-class'
              },
              label: {
                borderColor: '#FF4560',
                offsetY: 0,
                style: {
                  color: '#fff',
                  background: '#FF4560',
                },

                text: 'Point Annotation',
              }
            }]
          }]
        }
      })
      setSeries([
        {
          name: "NPV",
          data: props.y
        }])
  },[props.x,props.y]);



    return (
      <div className="app">
        <div className="row">
          <div className="mixed-chart">
            <Chart
              options={options}
              series={series}
              type="line"
              width="500"
            />
          </div>
        </div>
      </div>
    );
  }


export default PositionChart;
