import React,{useEffect,useState} from 'react';
//import ApexCharts from 'apexcharts'

import Chart from "react-apexcharts";

function AlgoPNLChart(props){

  const [options,setOptions] = useState({});
  const [series,setSeries] = useState([]);

  useEffect(()=>{
    setOptions({
      chart: {
        toolbar: {
          show: true,
          tools: {
            download: true,
            selection: true,
            zoom: true,
            zoomin: true,
            zoomout: true,
            pan: true,
            reset: true,
            customIcons: []
          },
          autoSelected: 'zoom' 
        },
      },
      xaxis:{
        type: "datetime"
      }

      })
      setSeries([
        {
          name: "PNL",
          data: props.chart
        }])
  },[props.chart]);



    return (
      <div className="app">
        <div >
          <div className="mixed-chart">
            <Chart
              options={options}
              series={series}
              type="area"
              width="100%"
            />
          </div>
        </div>
      </div>
    );
  }


export default AlgoPNLChart;
