import React,{useState,useEffect} from 'react';
import Positions from './Positions'
import {Container,Row,Col,Accordion, Button, Card} from 'react-bootstrap';
import PositionChart from './PositionChart'
import Summary from './Summary'
import CurrentAlgos from './CurrentAlgos'
import config from '../config';

function Dashboard(props) {
  const [summary,setSummary] = useState([]);
  const [npvX,setNpvX] = useState([]);
  const [npvY,setNpvY] = useState([]);
  const [email,setEmail] = useState({});

  useEffect(()=>{
    fetchData()
  },[]);

  const fetchData = async() => {
    setEmail(props.location.state)
    console.log(config.apiGateway.URL)
    console.log(JSON.stringify({email}))
    await fetch(config.apiGateway.URL+"/getsummary", {
        method: 'POST', // or 'PUT'
        body: JSON.stringify({email}), // data can be `string` or {object}!
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json'
        }
      })
      .then(response=>response.json())
      .then(data=>{
        console.log("API BEING USED IS:")
        console.log(config.apiGateway.URL)
        setSummary(data)
        setNpvX(Object.keys(data.npvchart))
        setNpvY(Object.values(data.npvchart))
      })
      .catch(error=>console.log(error));
    }

  return (

    <Container>
      <br></br>
      <br></br>
      <Row>
        <Col><Summary summary={summary}/></Col>
        <Col><PositionChart x={npvX} y={npvY}/></Col>

      </Row>
      <br></br>
      <Accordion defaultActiveKey="0">
        <Card>
          <Card.Header>

            <Accordion.Toggle as={Button} variant="link" eventKey="0">
              Algos
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="0">
            <Card.Body><CurrentAlgos summary={summary} email={email}/></Card.Body>
          </Accordion.Collapse>
        </Card>
        <Card>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="1">
              Current Positions
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="1">
            <Card.Body><Positions /></Card.Body>
          </Accordion.Collapse>
        </Card>
        <Card>
          <Card.Header>
            <Accordion.Toggle as={Button} variant="link" eventKey="2">
              Trade History
            </Accordion.Toggle>
          </Card.Header>
          <Accordion.Collapse eventKey="2">
            <Card.Body><Positions /></Card.Body>
          </Accordion.Collapse>
        </Card>
      </Accordion>
    </Container>
  );
}

export default Dashboard;
