import React,{useState,useEffect} from 'react';
import {Button,Modal,Table} from 'react-bootstrap';
import AddAmountModal from './AddAmountModal';
import AlgoDetailsModal from './AlgoDetailsModal';
import { USDFormat } from '../libs/numberFormat';
import config from '../config';

function AllAlgos(props) {
  const [currentAlgos,setCurrentAlgos] = useState([]);
  const [refresh,setRefresh] = useState(false);
  const [show, setShow] = useState(false);
  const handleClose = () => {props.refresh();setShow(false);}
  const handleShow = () => {setRefresh(!{refresh}.refresh);setShow(true);}

  const handleRefresh = () => {
    console.log("Calling all algo refresh")
    props.refresh()
    setRefresh(!{refresh}.refresh);
  }

  useEffect(()=>{
    const data = {"email":props.email}
    fetch(config.apiGateway.URL+"/getallalgos", {
      method: 'POST', // or 'PUT'
      body: JSON.stringify(data), // data can be `string` or {object}!
      headers: {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      }
    })
      .then(response=>response.json())
      .then(data=>{
        console.log("Fetching All Algos API")
        setCurrentAlgos(data)
      })
  },[refresh,props.email,props.refreshDash]);



  return (

    <div>
      <Button variant="primary" onClick={handleShow}>
        Add New Algo
      </Button>
      <Modal size="lg" show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>{props.name}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>Name</th>
              <th>Amount Allocated</th>
              <th>Profit/Loss</th>
              <th>-</th>
              <th>-</th>
            </tr>
          </thead>
          <tbody>

              {currentAlgos.map((item,key)=>(
                <tr key ={key}>
                  <td>{item.name}</td>
                  <td>{USDFormat(item.amount)}</td>
                  <td>{USDFormat(item.pnl)}</td>
                  <td>

                  <AddAmountModal email={props.email} name={item.name} refresh ={handleRefresh} min={5000} max={props.summary.amountfree}/>
                  </td> 
                  <td>
                  <AlgoDetailsModal name={item.name} email={props.email} refresh ={handleRefresh} />
                  </td>
                </tr>
                ))}

          </tbody>
        </Table>


        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

    </div>
  );
}

export default AllAlgos;
