import React,{useState} from 'react';
import {Card,Form,Button,Container,Row,Col} from 'react-bootstrap';
import { Auth } from "aws-amplify";



function Login(props) {
  const [email,setEmail] = useState("");
  const [password,setPassword] = useState("");

  function validateForm() {
    return email.length > 0 && password.length > 0;
  }

  async function handleSubmit(event) {
    event.preventDefault();

    try {
      await Auth.signIn(email, password);
      props.userHasAuthenticated(true);
      props.history.push({pathname:"/dashboard",state:{email}});
    } catch (e) {
      alert(e.message);
    }
  }


  return (
    <Container>
      <br></br>
      <Row>

        <Col></Col>
        <Col md="auto">
        <Card bg="light">
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formBasicEmail" bssize="large" >
              <Form.Label>Email</Form.Label>
              <Form.Control autoFocus value={email} type="email" placeholder="Enter email" onChange={e => setEmail(e.target.value)}/>

            </Form.Group>

            <Form.Group bssize="large" controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control value={password} type="password" placeholder="Password" onChange={e => setPassword(e.target.value)}/>
            </Form.Group>
            <br></br>
            <Button variant="info" disabled={!validateForm()} type="submit">
              Submit
            </Button>
          </Form>
          </Card>
        </Col>
        <Col></Col>

      </Row>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
    </Container>

  );
}

export default Login;
